module.exports = (node, rule, utils) => {
  if (node.parent.type === "PAGE") {
    return;
  }
  const parentName = utils.normalizeName(node.parent.name);
  if (["p-grid", "sidebar", "p-card", 'p-tabview-panels'].includes(parentName)) {
    
    if (node.children.length > 1) {
      utils.isHorizontalLayout(node);
      utils.checkSpacing(node, {
        type: "checkSpacing",
        value: 16,
      });
    }
    if (node.layoutMode === "HORIZONTAL") {
      utils.checkPadding(node, {
        value: [0],
        description: `Геометрия дочерних для <b>${parentName}</b> горизонтальных фреймов не учитывается. 
          Для задания отступов необходимо добавить еще один фрейм внутрь этого горизонтального фрейма`,
      });
    }
  }
};

