grid-td.source:
module.exports = (node, rule, utils) => {
  const {ErrorTypes} = utils;
  if (node.mainComponent) {
    const props = utils.parseComponentProps(node.mainComponent.name);
    if (props.Type === "Checkbox" && node.width !== 36) {
      utils.createErrorObject(
        node,
        ErrorTypes.STRUCTURE,
        `Для узла <b>grid-td</b> с <b>Type = 'Checkbox'</b>, ширина должна быть задана <b>36px</b>`
      );
    }
  }
};

