module.exports = (node, rule, utils) => {
  const parentName = utils.normalizeName(node.parent.name);
  if (parentName === "grid") {
    utils.containsChildren(node, {
      type: "containsChildren",
      names: [
        { name: "divider", maxcount: 2 },
        { name: "tr-group", mincount: 1 },
        { name: "tr-group-expanded", maxcount: 1 },
      ],
    });
  } else if (parentName === "table") {
    utils.containsChildren(node, {
      type: "containsChildren",
      names: [
        { name: "divider", maxcount: 1 },
        { name: "tr", maxcount: 1 },
        { name: "tr-group", maxcount: 1 },
      ],
    });
  }
};

