module.exports = (node, rule, utils) => {
  
  const pivotStackHorizontal = utils.findNested(node, "pivot-stack-horizontal");
  const pivotVerts = utils.findNested(node, "pivot-vert");
  if (pivotStackHorizontal.length && !pivotVerts.length) {
    utils.isVerticalLayout(node);
  }
  if (!pivotStackHorizontal.length && pivotVerts.length) {
    utils.isHorizontalLayout(node);
  }
};

p-tabview-nav.source:
module.exports = (node, rule, utils) => {
  const { ErrorTypes } = utils;
  
  const pivotVerts = utils.findNested(node, "pivot-vert");
  
  if (pivotVerts.length) {
    const activeVerts = pivotVerts.filter((item) => {
      if (item.mainComponent) {
        return (
          utils.parseComponentProps(item.mainComponent.name).State === "Active"
        );
      }
    });
    if (activeVerts.length !== 1) {
      utils.createErrorObject(
        node,
        ErrorTypes.STRUCTURE,
        `Для узла <b>p-tabview-nav</b> должен быть указан один вложенный компонент <b>pivot-vert</b> с состоянием <b>State = "Active"</b>`
      );
    }
  }
  
};

