table.source:
module.exports = (node, rule, utils) => {
  const bodyList = node.children.filter(
    (item) => utils.normalizeName(item.name) === "p-datatable-tbody"
  );
  const { ErrorTypes } = utils;
  if (bodyList.length) {
    const bodyHeight = bodyList[0].height;
    const wrongHeight = bodyList.filter((item) => item.height !== bodyHeight);
    if (wrongHeight.length) {
      utils.createErrorObject(
        node,
        ErrorTypes.PADDING,
        "Все вложенные элементы <b>p-datatable-tbody</b> должны иметь одинаковую высоту"
      );
    }
  }
};

