tr.source:
module.exports = (node, rule, utils) => {
  const container = utils.findParent(node, ["grid", "table"]);
  const parentName = utils.normalizeName(node.parent.name);
  if (container) {
    const containerName = utils.normalizeName(container.name);
    if (containerName === "grid") {
      utils.isChildrenOf(node, {
        type: "isChildrenOf",
        parent: ["tr-group", "tr-group-expanded"],
      });

      if (parentName === "p-datatable-thead") {
        utils.containsChildren(node, {
          type: "containsChildren",
          names: ["grid-th"],
          nodeName: "grid => p-datatable-thead => tr",
        });
      }
      if (parentName === "p-datatable-tbody") {
        utils.containsChildren(node, {
          type: "containsChildren",
          names: ["grid-td"],
          nodeName: "grid > p-datatable-tbody > tr",
        });
      }

      utils.checkPadding(node, {
        type: "checkPadding",
        value: [0],
        description:
          " Горизонтальные отступы необходимо задавать в родительском <b>tr-group</b>",
      });
    } else if (containerName === "table") {
      utils.isChildrenOf(node, {
        type: "isChildrenOf",
        parent: ["p-datatable-thead", "p-datatable-tbody", "tr-group"],
      });

      if (parentName === "p-datatable-thead") {
        utils.containsChildren(node, {
          type: "containsChildren",
          names: ["table-th"],
          nodeName: "table > p-datatable-thead > tr",
        });
      }
      if (parentName === "p-datatable-tbody") {
        utils.containsChildren(node, {
          type: "containsChildren",
          names: ["table-td"],
          nodeName: "table > p-datatable-tbody > tr",
        });
      }
    }
  }
};
